    def parse_css_font_url(self):
        '''
        解析字体css文件
        :return:
        '''
        response = session.get(self.css_font_url, headers=self.headers).content.decode()
        # print(response)
        font_info_list = re.findall(
            r'font-family: "(.*?)";src:.*?(//s3plus\.meituan\.net/v1/mss_73a511b8f91f43d0bdae92584ea6330b/font/\w+.woff)',
            response)
        '''获取字体标签名称，字体文件url'''
        # print(font_info_list)
        font_name_list = [font[0].split('-')[-1] for font in font_info_list]
        font_url_list = ['https:' + font[1] for font in font_info_list]
        # 创建字体字典，根据字体加密的种类，对应字体的数据解析
        class_name_dict = {}
        for font_name, font_url in zip(font_name_list, font_url_list):
            # print(font_name, font_url)
            self.parse_font_url(font_name, font_url, class_name_dict)
            # pass
        print(class_name_dict)
        self.parse_start_url(class_name_dict)

    def parse_font_url(self, font_name, font_url, class_name_dict):
        '''
        解析字体文件，获取字体编码信息
        :param font_name: 标签名称
        :param font_url: 字体解析url
        :return:
        '''
        # 获取字体url响应
        response = session.get(font_url).content
        '''1.创建未解析的字体文件的名称和路径'''
        file_path = self.os_path + font_name + '.woff'
        with open(file_path, 'wb')as f:
            f.write(response)
        '''解析字体'''
        font_data = TTFont(file_path)
        file_path_2 = self.os_path + '解析-' + font_name + '.woff'
        font_data.saveXML(file_path_2)
        '''        
         网页源码中的字体 &#xf7c6                               英文
         下载下来的字体文件通过字体工具打开  0123456789我爱你      中文
         字体译文源码结构                                       拼音
        '''
        # 从xml字体源码取出所有的name值
