    def tanslate_content(self, data, class_name_dict, class_name):
        '''
        解析网页源码字体，通过字典取值，翻译字体
        :param data:            需解密的字体
        :param class_name_dict: 突破字体反爬大字典
        :param class_name:      标签名称，区分不同的字体类型解密
        :return:
        '''
        list_1 = []
        for index, cont_str in enumerate(data):
            # repr防止被转义
            res = repr(cont_str).replace(r'\u', '').replace("'", '')
            # print(len(res), res, type(res))
            if len(res) != 4:
                list_1.append(res)
            else:
                try:
                    result = class_name_dict[class_name][res]
                    list_1.append(result)
                except Exception as e:
                    print(e)
                    list_1.append(res)
        # print(list_1)
        content = ''.join(list_1)
        # print(content)
        return content

